window.addEventListener("mousemove",function(e){var offsetX=(20*(1-(document.body.clientWidth-e.clientX)/document.body.clientWidth)+40).toFixed(2),offsetY=(20*(1-(document.body.clientHeight-e.clientY)/document.body.clientHeight)+40).toFixed(2);$("#LoginForm").css({backgroundPositionX:offsetX+"%",backgroundPositionY:offsetY+"%"})},!1),window.addEventListener("deviceorientation",function(eventData){var yTilt=(20*(1-(-eventData.beta+90)/180)+40).toFixed(2),xTilt=(20*(1-(-eventData.gamma+90)/180)+40).toFixed(2);$("#LoginForm").css({backgroundPositionX:xTilt+"%",backgroundPositionY:yTilt+"%"})},!1);